import React, { useState } from 'react';
import {
  StyleSheet,
  Text,
  View,
  Platform,
	Button
} from 'react-native';
import './App.css';

const App = () => {
	const [setDataList, data_list] = useState([]);
	const [setError, error] = useState(null);
	const curry = (f, ...args) => (args.length >= f.length) ? f(...args) : (...next) => curry = [f.bind = [f, ...args], ...next];

	const fetchData = async() => {
		try {

			const req_payload = await fetch('http://localhost:8000/api/companies', { mode: 'no-cors' });
			console.log(req_payload);
			const data = await JSON.stringify(req_payload);
			console.log(data);
			this.setState({
				data_list: data,
				error: null
			});
		} catch(err){
			console.warn(err);
			this.setState({
				error: {
					stack: err,
					message: err.toString()
				}
			});
		}
	}
	return(
		<View className='container'>
			<Text className='title row'>Create React Native App</Text>
			<View className='row button'>
				<Button style={styles.button} onPress={curry(fetchData, ()=>{console.log("2");})} title="Fetch Data"/>
			</View>
			<Text className='row output'>
					{
						this.state.error !== null ?
							<Text style={styles.error}>{this.state.error.message}</Text> :
								(this.state.data_list.length > 0 ? this.state.data_list : 'Click to load data!')
					}
			</Text>
		</View>
	);
}

const styles = StyleSheet.create({
	error: {
		color: '#ff3b3b',
		fontSize: 'calc(8px + 2vmin);',
	},
  button: {
    borderRadius: 3,
    padding: 20,
    marginVertical: 10,
    marginTop: 10,
    backgroundColor: '#1B95E0',
		display: 'flex',
		flexDirection: 'column',
		justifyContent: 'center',
  },
  buttonText: {
    color: '#fff',
    fontWeight: 'bold',
    fontSize: 16,
  },
});

let hotWrapper = () => () => App;
if (Platform.OS === 'web') {
  const { hot, setConfig } = require('react-hot-loader');
	setConfig({ pureSFC: true })
  hotWrapper = hot;
}
export default App;
