import React, { Component } from 'react';
import {
  StyleSheet,
  Text,
  View,
  Platform,
  TouchableHighlight,
  Animated,
  Easing,
	Button
} from 'react-native';
import logo from './logo.png';

class App extends Component {
  state = {
		data_list: [],
		error: null
	}

	fetchData = async() => {
		try {
			const req_payload = await fetch('http://127.0.0.1:8000/api/company');
			const data = await req_payload.json();
			console.log("data", data);
			this.setState({
				data_list: data.results
			});
		} catch(err){
			this.setState({
				error: err
			});
			console.warn(err);
		}
	}

	handleChange = (e) => {
		console.warn("handling change");
		return this.fetchData();
	}

  render() {
		return(
			<View style={styles.container}>
				<Text style={styles.title}>Create React Native Web App</Text>
				<Button style={styles.button} onPress={this.handleChange} title="Fetch Data"/>
				<div>{JSON.stringify(this.state.data_list)}</div>
			</View>
		);
  }
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
  },
  logo: {
    width: 300,
    height: 300,
  },
  title: {
    fontWeight: 'bold',
    fontSize: 16,
  },
  button: {
    borderRadius: 3,
    padding: 20,
    marginVertical: 10,
    marginTop: 10,
    backgroundColor: '#1B95E0',
  },
  buttonText: {
    color: '#fff',
    fontWeight: 'bold',
    fontSize: 16,
  },
});

let hotWrapper = () => () => App;
if (Platform.OS === 'web') {
  const { hot } = require('react-hot-loader');
  hotWrapper = hot;
}
export default hotWrapper(module)(App);
