import React, { Component } from 'react';
import {
  StyleSheet,
  Text,
  View,
  Platform,
  TouchableHighlight,
  Animated,
  Easing,
	Button
} from 'react-native';
import logo from './logo.png';
import './App.css';

class App extends Component {
  state = {
		data_list: [],
		error: null
	}

	fetchData = async() => {
		try {
			const req_payload = await fetch('http://127.0.0.1:8000/api/company');
			const data = await req_payload.json();
			console.log("data", data);
			this.setState({
				data_list: data.results,
				error: null
			});
		} catch(err){
			console.warn(err);
			this.setState({
				error: {
					stack: err,
					message: err.toString()
				}
			});
		}
	}

	handleChange = (e) => {
		console.warn("handling change");
		return this.fetchData();
	}

  render() {
		return(
			<View className='container'>
				<div className='title row'>Create React Native Web App</div>
				<div className='row button'>
					<Button style={styles.button} onPress={this.handleChange} title="Fetch Data"/>
				</div>
				<div className='row'>
					<Text style={styles.output}>
						{
							this.state.error !== null ?
								<Text style={styles.error}>{this.state.error.message}</Text> :
									(this.state.data_list.length > 0 ? JSON.stringify(this.state.data_list) : '')
						}
					</Text>
				</div>
			</View>
		);
  }
}

const styles = StyleSheet.create({
  logo: {
    width: 300,
    height: 300,
  },
	output: {
		textAlign: 'center'
	},
	error: {
		height: '10%',
		color: '#ff3b3b',
		fontSize: 'calc(10px + 2vmin);',
	},
  button: {
		height: '100%',
    borderRadius: 3,
    padding: 20,
    marginVertical: 10,
    marginTop: 10,
    backgroundColor: '#1B95E0',
		display: 'flex',
		flexDirection: 'column',
		justifyContent: 'center',
  },
  buttonText: {
    color: '#fff',
    fontWeight: 'bold',
    fontSize: 16,
  },
});

let hotWrapper = () => () => App;
if (Platform.OS === 'web') {
  const { hot } = require('react-hot-loader');
  hotWrapper = hot;
}
export default hotWrapper(module)(App);
